import React from "react";
import ReactDOM from "react-dom";

const Header = (props) => {
  return <h1>{props.course}</h1>;
};

const Content = (parts) => {
  const Part = (props) => (
    <p>
      Part {props.name}. Number of excercise {props.exercises}
    </p>
  );
  return (
    <div>
      <Part props={parts[0]} />
      <Part props={parts[1]} />
      <Part props={parts[2]} />
    </div>
  );
};

const Total = (parts) => {
  let sum = 0;
  parts.forEach((item) => sum + item.exercises);
  return (
    <div>
      <p>Number of exercises {sum}</p>
    </div>
  );
};

const App = () => {
  const course = "Half Stack application development";
  const parts = [
    {
      name: "Fundamentals of React",
      exercises: 10,
    },
    {
      name: "Using props to pass data",
      exercises: 7,
    },
    {
      name: "State of a component",
      exercises: 14,
    },
  ];

  return (
    <div>
      <Header course={course} />
      <Content parts={parts} />
      <Total parts={parts} />
    </div>
  );
};

ReactDOM.render(
  <React.StrictMode>
    <App />
  </React.StrictMode>,
  document.getElementById("root")
);
