import React, { useState, useEffect } from "react";
import ReactDOM from "react-dom";
import axios from "axios";

const App = () => {
  const [countries, setCountries] = useState([]);
  const [searchTerm, setSearchTerm] = useState("");
  useEffect(() => {
    axios.get("https://restcountries.eu/rest/v2/all").then((response) => {
      setCountries(response.data);
      console.log(response.data);
    });
  }, []);

  const handleInput = (event) => {
    setSearchTerm(event.target.value);
  };

  const listCountry = (countries) => {
    const filteredCountries = countries?.filter((country) => {
      return country.name.toLowerCase().startsWith(searchTerm.toLowerCase());
    });
    console.log(searchTerm);
    console.log(countries);
    console.log(filteredCountries);
    if (filteredCountries.length > 0 && filteredCountries.length < 10) {
      return filteredCountries.map((country, index) => (
        <p key={index}>{country.name}</p>
      ));
    }
    if (filteredCountries.length >= 10) {
      return <p>Too many matches, specify another filter</p>;
    }
    return <p>No countries found</p>;
  };

  return (
    <div>
      <form>
        <label htmlFor="country">Find country</label>
        <br />
        <input
          type="text"
          id="country"
          name="country"
          value={searchTerm}
          onChange={handleInput}
        />
      </form>
      <div>{listCountry(countries)}</div>
    </div>
  );
};

export default App;

ReactDOM.render(
  <React.StrictMode>
    <App />
  </React.StrictMode>,
  document.getElementById("root")
);
