      ));
    }
    if (filteredCountries.length >= 10) {
      return <p>Too many matches, specify another filter</p>;
    }
    return <p>No countries found</p>;
  };

  return (
    <div>
      <form>
        <label htmlFor="country">Find country</label>
        <br />
        <input
          type="text"
          id="country"
          name="country"
          value={searchTerm}
          onChange={handleInput}
        />
      </form>
      <div>{listCountry(countries)}</div>
    </div>
  );
};

export default App;

ReactDOM.render(
  <React.StrictMode>
    <App />
  </React.StrictMode>,
