import React, { useState } from "react";
import ReactDOM from "react-dom";
import Filter from "./Filter";
import PersonForm from "./PersonForm";
import Persons from "./Persons";

const App = () => {
  const [persons, setPersons] = useState([
    { name: "Arto Hellas", number: "040-123456" },
    { name: "Ada Lovelace", number: "39-44-5323523" },
    { name: "Dan Abramov", number: "12-43-234345" },
    { name: "Mary Poppendieck", number: "39-23-6423122" },
  ]);
  const defaultValue = { name: "", number: "" };
  const [newName, setNewName] = useState(defaultValue);
  const [filteredName, setFilteredName] = useState();

  const handleSubmit = (event) => {
    event.preventDefault();
    setPersons([...persons, newName]);
    setNewName(defaultValue);
  };

  const handleInput = (event) => {
    setNewName({ ...newName, [event.target.name]: event.target.value });
  };

  const handleFilter = (event) => {
    setFilteredName([
      ...persons,
      persons.filter((person) => {
        return person.name.toLowerCase() === event.target.value.toLowerCase();
      }),
    ]);
  };

  return (
    <div>
      <h2>Phonebook</h2>
      <Filter filteredName={filteredName} handleFilter={handleFilter} />
      <h2>Add a new</h2>
      <PersonForm
        newName={newName}
        handleSubmit={handleSubmit}
        handleInput={handleInput}
      />
      <h2>Numbers</h2>
      <Persons persons={persons} />
    </div>
  );
};

export default App;

ReactDOM.render(
  <React.StrictMode>
    <App />
  </React.StrictMode>,
  document.getElementById("root")
);
