import React, { useState, useEffect } from "react";
import ReactDOM from "react-dom";
import Filter from "./Filter";
import PersonForm from "./PersonForm";
import Persons from "./Persons";
import * as dataServices from "./services/dataServices";

const App = () => {
  const defaultValue = { name: "", number: "" };
  const [persons, setPersons] = useState([]);
  const [newPerson, setNewPerson] = useState(defaultValue);
  const [filteredName, setFilteredName] = useState("");

  useEffect(() => {
    dataServices.getAll().then((data) => setPersons(data));
  }, []);

  const handleSubmit = (event) => {
    event.preventDefault();
    const isPersonExist = persons?.find((person) => {
      return person.name === newPerson.name;
    });
    const addPerson = (name) => {
      dataServices.create(name).then((name) => {
        setPersons(...persons, name);
        setNewPerson(defaultValue);
      });
    };
    isPersonExist
      ? alert(`${newPerson.name} is already added to phonebook`)
      : setPersons(...persons, newPerson);
    addPerson(newPerson);
    setNewPerson(defaultValue);
  };

  const handleInput = (event) => {
    setNewPerson({ ...newPerson, [event.target.name]: event.target.value });
  };

  const handleFilter = (event) => {
    setFilteredName(event.target.value);

    const filteredPerson = persons?.filter((person) => {
      return person.name
        .toLocaleLowerCase()
        .includes(filteredName?.toLocaleLowerCase());
    });

    filteredName.length > 0 ? setPersons(filteredPerson) : setPersons(persons);
  };

  return (
    <div>
      <h2>Phonebook</h2>
      <Filter filteredName={filteredName} handleFilter={handleFilter} />
      <h2>Add a new</h2>
      <PersonForm
        newPerson={newPerson}
        handleSubmit={handleSubmit}
        handleInput={handleInput}
      />
      <h2>Numbers</h2>
      <Persons persons={persons} />
    </div>
  );
};

export default App;

ReactDOM.render(
  <React.StrictMode>
    <App />
  </React.StrictMode>,
  document.getElementById("root")
);
