import React, { useState, useEffect } from "react";
import ReactDOM from "react-dom";
import axios from "axios";
import Filter from "./Filter";
import PersonForm from "./PersonForm";
import Persons from "./Persons";

const App = () => {
  const defaultValue = { name: "", number: "" };
  const [persons, setPersons] = useState([]);
  const [newName, setNewName] = useState(defaultValue);
  const [filteredName, setFilteredName] = useState("");

  useEffect(() => {
    axios.get("http://localhost:3001/persons").then((response) => {
      setPersons(response.data);
    });
  }, []);

  const handleSubmit = (event) => {
    event.preventDefault();
    const isPersonExist = persons.find((person) => {
      return person.name === newName.name;
    });
    isPersonExist
      ? alert(`${newName.name} is already added to phonebook`)
      : setPersons([...persons, newName]);
    setNewName(defaultValue);
  };

  const handleInput = (event) => {
    setNewName({ ...newName, [event.target.name]: event.target.value });
  };

  const handleFilter = (event) => {
    setFilteredName(event.target.value);

    const filteredPerson = persons?.filter((person) => {
      return person.name
        .toLocaleLowerCase()
        .includes(filteredName?.toLocaleLowerCase());
    });

    filteredName.length > 0 ? setPersons(filteredPerson) : setPersons(persons);
  };

  return (
    <div>
      <h2>Phonebook</h2>
      <Filter filteredName={filteredName} handleFilter={handleFilter} />
      <h2>Add a new</h2>
      <PersonForm
        newName={newName}
        handleSubmit={handleSubmit}
        handleInput={handleInput}
      />
      <h2>Numbers</h2>
      <Persons persons={persons} />
    </div>
  );
};

export default App;

ReactDOM.render(
  <React.StrictMode>
    <App />
  </React.StrictMode>,
  document.getElementById("root")
);
